// This script will log the received GPS signals.

while (true) {
    const gps = $receive(0) // GPS.Data is stream. This call waits until a new 
    // value is received.

    let accuracy = gps.accuracy
    let lat = gps.latitude
    let lon = gps.longitude
    let alt = gps.altitude

    $log("Accuracy: " + accuracy + " (" + lat + ", " + lon + ", " + alt + ")")
}
